if ( ! function_exists('cac_ajax_menu_content') ) {

// Register Custom Post Type for content to be loaded
function cac_ajax_menu_content() {

	$labels = array(
		'name'                => 'AJAX Contents',
		'singular_name'       => 'AJAX Content',
		'menu_name'           => 'AJAX Content',
		'all_items'           => 'All AJAX Content',
		'view_item'           => 'View AJAX Content',
		'add_new_item'        => 'Add New AJAX Content',
		'add_new'             => 'Add New',
		'edit_item'           => 'Edit AJAX Content',
		'update_item'         => 'Update AJAX Content',
		'search_items'        => 'Search AJAX Content',
		'not_found'           => 'Not found',
		'not_found_in_trash'  => 'Not found in Trash',
	);
	$args = array(
		'label'               => 'ajaxmenucontent',
		'description'         => 'Create content to load dynamically here.',
		'labels'              => $labels,
		'supports'            => array( 'title', 'editor', ),
		'hierarchical'        => false,
		'public'              => true,
		'show_ui'             => true,
		'show_in_menu'        => true,
		'show_in_nav_menus'   => true,
		'show_in_admin_bar'   => true,
		'menu_position'       => 5,
		'can_export'          => true,
		'has_archive'         => false,
		'exclude_from_search' => true,
		'publicly_queryable'  => true,
		'rewrite'             => false,
		'capability_type'     => 'page',
	);
	register_post_type( 'ajaxmenucontent', $args );

}

// Hook into the 'init' action to register post type
add_action( 'init', 'cac_ajax_menu_content', 0 );

}

// Register Frontend Scripts
function cac_ajax_js() {

	wp_register_script( 'menuitemfunction', plugin_dir_url( __FILE__ ) . 'js/menu-item-function.js', array( 'jquery' ), '0.0.1' );
	wp_localize_script( 'menuitemfunction', 'cacAjax', array( 'ajaxurl' => admin_url( 'admin-ajax.php' ))); 
	wp_enqueue_script( 'menuitemfunction' );

}

// Hook into the 'wp_enqueue_scripts' action
add_action( 'wp_enqueue_scripts', 'cac_ajax_js' );

// Add Shortcode for creating target element; enclose content if it exists
function cac_ajax_target( $atts , $content = null ) {
	$html = '<div class="cac_ajax_target">';
	if( $content != null ) {
	$html .= $content;
	}
	$html .= '</div>';
	return $html;
}
add_shortcode( 'ajaxtarget', 'cac_ajax_target' );

add_action("wp_ajax_cac_return_post", "cac_return_post");
add_action("wp_ajax_nopriv_cac_return_post", "cac_return_post");

//function returns post content of an ajaxmenucontent post, with shortcodes processed, to AJAX request from front end
function cac_return_post() {

   	$slug = $_REQUEST["slug"];
	$args = array(
  		'name' => $slug,
  		'post_type' => 'ajaxmenucontent',
  		'post_status' => 'publish',
  		'numberposts' => 1
	);
	$thepost = get_posts($args);
	
   if($thepost === false) {
      $result['type'] = "error";
   }
   else {
      $result['type'] = "success";
      $result['post'] = do_shortcode($thepost[0]->post_content);
   }

   if(!empty($_SERVER['HTTP_X_REQUESTED_WITH']) && strtolower($_SERVER['HTTP_X_REQUESTED_WITH']) == 'xmlhttprequest') {
      $result = json_encode($result);
      echo $result;
   }
   else {
      header("Location: ".$_SERVER["HTTP_REFERER"]);
   }

   die();

}